export default {
    // 网站信息
    websiteInfo: {
        name: 'ZHOUYI',
        version: '1.0.0',
        desc: '管理平台',
        // logo仅支持在线地址
        logo: 'https://element-plus.org/images/element-plus-logo.svg',
    },
    // 主题配置
    theme: {
        // 主题颜色
        // color: '#409EFF',
        color: '#3f51b5',
        // 主题大小
        size: 'small', // default, small, mini
    },
    // 快捷键
    // 支持快捷键 例如 ctrl+shift+s
    hotkey: {
        search: {
            open: 's',
            close: 'esc'
        }
    },
    // 页脚备案信息
    reference: {
        show: true, //是否展示页脚
        number: '备案号-1',//备案号
        authorization: 'ZHOUYI', // 站点所属机构
        authorizationUrl: 'https://www.zhouyi.run', // 站点所属机构链接
    },

}
